import Container from "@mui/material/Container";
import Button from "@mui/material/Button";
import TextField from "@mui/material/TextField";
import Task from "./Task";
import { tasksContext } from "../context/TaskContext";

export default function TodoList() {
  let Tasks = [
    {
      id: 1,
      text: "زيد الخير الحداد",
      taskDetails: "يجب إنجاز هذا المشروع خلال يومين",
      isDone: false,
    },
    {
      id: 2,
      text: "أحمد عرقسوسي",
      taskDetails: "يجب إنجاز هذا المشروع خلال يومين",
      isDone: false,
    },
  ];
  let task = Tasks.map((t) => {
    return (
      <tasksContext.Provider value={Tasks}>
        <Task />
      </tasksContext.Provider>
    );
  });

  return (
    <Container
      maxWidth="xs"
      style={{
        backgroundColor: "#eee",
        marginTop: "150px",
        padding: "10px 20px",
        borderRadius: "6px",
        // maxHeight: "200px",
      }}
    >
      <h1 style={{ fontFamily: "A" }}>مهامي</h1>

      <div style={{ marginBottom: "20px" }}>
        <Button variant="outlined" color="error">
          غير منجز
        </Button>
        <Button variant="outlined" color="error">
          منجز
        </Button>
        <Button variant="outlined" color="error">
          الكل
        </Button>
      </div>

      {task}

      <div style={{ display: "flex", justifyContent: "center" }}>
        <Button
          variant="contained"
          color="error"
          style={{ width: "30%", marginRight: "5px" }}
        >
          إضافة
        </Button>

        <TextField
          id="outlined-basic"
          label="عنوان المهمة"
          variant="outlined"
          color="error"
          style={{ flex: "1" }}
        />
      </div>
    </Container>
  );
}
