import Container from "@mui/material/Container";
import Button from "@mui/material/Button";
import TextField from "@mui/material/TextField";
import Task from "./Task";
import { tasksContext } from "../context/TaskContext";
import { useState, useContext, useEffect } from "react";
import { v4 as uuidv4 } from "uuid";

export default function TodoList() {
  const { todo, setTodo } = useContext(tasksContext);
  const [tasksShow, setTaskShow] = useState("all");
  const [titleInput, setTitleInput] = useState("");

  const initialTasks = [
    {
      id: uuidv4(),
      title: "زيد الخير الحداد",
      taskDetails: "يجب إنجاز هذا المشروع خلال يومين",
      isDone: false,
    },
  ];

  useEffect(() => {
    const storedTodos = JSON.parse(localStorage.getItem("todos"));
    if (storedTodos) {
      setTodo(storedTodos);
    } else {
      localStorage.setItem("todos", JSON.stringify(initialTasks));
      setTodo(initialTasks);
    }
  }, []);

  const filteredTodos = todo.filter((t) => {
    if (tasksShow === "done") return t.isDone;
    if (tasksShow === "notDone") return !t.isDone;
    return true; // "all"
  });

  // const allTodosList = todo.map((t) => {
  //   if (tasksShow === "done") {
  //     if (t.isDone) {
  //       return <Task key={t.id} todo={t} />;
  //     }
  //   } else if (tasksShow === "notDone") {
  //     if (!t.isDone) {
  //       return <Task key={t.id} todo={t} />;
  //     }
  //   } else {
  //     return <Task key={t.id} todo={t} />;
  //   }
  // });

  function handleAddClick() {
    if (titleInput !== "") {
      const newTodo = {
        id: uuidv4(),
        title: titleInput,
        taskDetails: "",
        isDone: false,
      };
      localStorage.setItem("todos", JSON.stringify([...todo, newTodo]));
      setTodo([...todo, newTodo]);
      setTitleInput("");
    }
  }

  return (
    <Container
      maxWidth="xs"
      style={{
        backgroundColor: "#eee",
        marginTop: "150px",
        padding: "10px 20px",
        borderRadius: "6px",
        overflow: "scroll",
        maxHeight: "70vh",
      }}
    >
      <h1 style={{ fontFamily: "A" }}>مهامي</h1>
      {/*  */}
      {/* أزرار التصنيفات */}
      {/*  */}
      <div style={{ marginBottom: "20px" }}>
        <Button
          variant="outlined"
          color="error"
          onClick={() => {
            setTaskShow("notDone");
          }}
        >
          غير منجز
        </Button>
        <Button
          variant="outlined"
          color="error"
          onClick={() => {
            setTaskShow("done");
          }}
        >
          منجز
        </Button>
        <Button
          variant="outlined"
          color="error"
          onClick={() => {
            setTaskShow("all");
          }}
        >
          الكل
        </Button>
      </div>
      {/*  */}
      {/* أزرار التصنيفات */}
      {/*  */}

      {filteredTodos.map((t) => (
        <Task key={t.id} todo={t} />
      ))}

      {/*  */}
      {/* إضافة مهمة جديدة */}
      {/*  */}
      <div style={{ display: "flex", justifyContent: "center" }}>
        <Button
          variant="contained"
          color="error"
          style={{ width: "30%", marginRight: "5px" }}
          onClick={() => {
            handleAddClick();
          }}
        >
          إضافة
        </Button>

        <TextField
          id="outlined-basic"
          label="عنوان المهمة"
          variant="outlined"
          color="error"
          onChange={(e) => {
            setTitleInput(e.target.value);
          }}
          value={titleInput}
          style={{ flex: "1" }}
        />
      </div>
      {/*  */}
      {/* إضافة مهمة جديدة */}
      {/*  */}
    </Container>
  );
}
