import Icons from "./Icons";
import "./Task.css";
import { useContext } from "react";
import { tasksContext } from "../context/TaskContext";
export default function Task() {
  let myTaskContext = useContext(tasksContext);
  tasksContext.forEach((task) => {
    return (
      <div
        key={task.id}
        className="text"
        style={{
          padding: "0px 5px",
          display: "flex",
          justifyContent: "left",
          alignItem: "center",
          backgroundColor: "#252F88",
          margin: "20px 0",
          transition: ".3s",
        }}
      >
        <Icons />
        <div
          style={{
            color: "white",
            flex: "1",
            marginLeft: "5px",
            textAlign: "right",
          }}
        >
          <h4>{task.text}</h4>
          <h6>{task.taskDetails}</h6>
        </div>
      </div>
    );
  });
