import Container from "@mui/material/Container";
import Button from "@mui/material/Button";
import TextField from "@mui/material/TextField";
import Task from "./Task";
import { tasksContext } from "../context/TaskContext";
import { useContext } from "react";

export default function TodoList() {
  let Tasks = [
    {
      id: 1,
      text: "زيد الخير الحداد",
      taskDetails: "يجب إنجاز هذا المشروع خلال يومين",
    },
  ];
  let myTaskContext = useContext(tasksContext);
  return (
    <myTaskContext.provider value={Tasks}>
      <Container
        maxWidth="xs"
        style={{
          backgroundColor: "#eee",
          marginTop: "150px",
          padding: "10px 20px",
          borderRadius: "6px",
        }}
      >
        <h1 style={{ fontFamily: "A" }}>مهامي</h1>

        <div style={{ marginBottom: "20px" }}>
          <Button variant="outlined" color="error">
            غير منجز
          </Button>
          <Button variant="outlined" color="error">
            منجز
          </Button>
          <Button variant="outlined" color="error">
            الكل
          </Button>
        </div>

        <Task />
        <div style={{ display: "flex", justifyContent: "center" }}>
          <Button
            variant="contained"
            color="error"
            style={{ width: "30%", marginRight: "5px" }}
          >
            إضافة
          </Button>

          <TextField
            id="outlined-basic"
            label="عنوان المهمة"
            variant="outlined"
            color="error"
            style={{ flex: "1" }}
          />
        </div>
      </Container>
    </myTaskContext.provider>
  );
}
