import CheckIcon from "@mui/icons-material/Check";
import ModeEditOutlineOutlinedIcon from "@mui/icons-material/ModeEditOutlineOutlined";
import DeleteOutlineIcon from "@mui/icons-material/DeleteOutline";
import IconButton from "@mui/material/IconButton";
import "./icon.css";
import "./Task.css";
import { useContext } from "react";
import { tasksContext } from "../context/TaskContext";

export default function Task({ todo }) {
  // استقبل todo من props
  const { todo: todos, setTodo } = useContext(tasksContext);

  function handleCheckClick() {
    let updatedTodos = todos.map((t) =>
      t.id === todo.id ? { ...t, isDone: !t.isDone } : t
    );
    setTodo(updatedTodos);
  }

  return (
    <div
      className="text"
      style={{
        padding: "0px 5px",
        display: "flex",
        justifyContent: "left",
        alignItems: "center",
        backgroundColor: "#252F88",
        margin: "20px 0",
        transition: ".3s",
      }}
    >
      <div
        style={{
          display: "flex",
          direction: "rtl",
          justifyContent: "space-around",
          alignItems: "center",
          fontSize: "6px",
        }}
      >
        {/* زر التحقق */}
        <IconButton aria-label="check" onClick={handleCheckClick}>
          <CheckIcon
            className="myIcon"
            style={{
              color: todo.isDone ? "white" : "#8bc34a",
              padding: "3px",
              background: todo.isDone ? "#8bc34a" : "white",
              border: "1px solid #8bc34a",
              borderRadius: "50%",
              fontSize: "20px",
              transition: ".3s",
            }}
          />
        </IconButton>

        {/* زر التعديل */}
        <IconButton aria-label="edit">
          <ModeEditOutlineOutlinedIcon
            className="myIcon"
            style={{
              color: "#1769aa",
              background: "white",
              border: "1px solid #1769aa",
              borderRadius: "50%",
              fontSize: "20px",
              transition: ".3s",
              padding: "3px",
            }}
          />
        </IconButton>

        {/* زر الحذف */}
        <IconButton aria-label="delete">
          <DeleteOutlineIcon
            className="myIcon"
            style={{
              background: "white",
              border: "1px solid red",
              borderRadius: "50%",
              fontSize: "20px",
              transition: ".3s",
              padding: "3px",
            }}
            color="error"
          />
        </IconButton>
      </div>

      {/* نص المهمة */}
      <div
        style={{
          color: "white",
          flex: "1",
          marginLeft: "5px",
          textAlign: "right",
        }}
      >
        <h4 style={{ textDecoration: todo.isDone ? "line-through" : "none" }}>
          {todo.title}
        </h4>
        <h6>{todo.taskDetails}</h6>
      </div>
    </div>
  );
}
