import CheckIcon from "@mui/icons-material/Check";
import ModeEditOutlineOutlinedIcon from "@mui/icons-material/ModeEditOutlineOutlined";
import DeleteOutlineIcon from "@mui/icons-material/DeleteOutline";
import IconButton from "@mui/material/IconButton";
import "./icon.css";
import "./Task.css";
// import { useContext } from "react";
// import { tasksContext } from "../context/TaskContext";

export default function Task({ todo, handleCheck }) {
  // let myTaskContext = useContext(tasksContext);
  // console.log(tasksContext);
  // console.log(myTaskContext);

  function handleCheckClick() {
    handleCheck(todo.id);
  }

  return (
    <div
      className="text"
      style={{
        padding: "0px 5px",
        display: "flex",
        justifyContent: "left",
        alignItems: "center",
        backgroundColor: "#252F88",
        margin: "20px 0",
        transition: ".3s",
      }}
    >
      <div
        style={{
          display: "flex",
          direction: "rtl",

          justifyContent: "space-around",
          alignItems: "center",
          fontSize: "6px",
        }}
      >
        <IconButton aria-label="delete">
          <CheckIcon
            className="myIcon"
            style={{
              color: "#8bc34a",
              padding: "3px",
              background: "white",
              border: "1px solid #8bc34a",
              borderRadius: "50%",
              fontSize: "20px",
              transition: ".3s",
            }}
            onClick={() => {
              handleCheckClick();
            }}
          />
        </IconButton>
        <IconButton aria-label="delete">
          <ModeEditOutlineOutlinedIcon
            className="myIcon"
            style={{
              color: "#1769aa",
              background: "white",
              border: "1px solid #1769aa",
              borderRadius: "50%",
              fontSize: "20px",
              transition: ".3s",
              padding: "3px",
            }}
          />
        </IconButton>
        <IconButton aria-label="delete">
          <DeleteOutlineIcon
            className="myIcon"
            style={{
              background: "white",
              border: "1px solid red",
              borderRadius: "50%",
              fontSize: "20px",
              transition: ".3s",
              padding: "3px",
            }}
            color="error"
          />
        </IconButton>
      </div>
      <div
        style={{
          color: "white",
          flex: "1",
          marginLeft: "5px",
          textAlign: "right",
        }}
      >
        <h4>{todo.title}</h4>
        <h6>{todo.details}</h6>
      </div>
    </div>
  );
}
