import CheckIcon from "@mui/icons-material/Check";
import ModeEditOutlineOutlinedIcon from "@mui/icons-material/ModeEditOutlineOutlined";
import DeleteOutlineIcon from "@mui/icons-material/DeleteOutline";
import IconButton from "@mui/material/IconButton";
import "./icon.css";
import "./Task.css";
import { useContext, useState } from "react";
import { tasksContext } from "../context/TaskContext";
import Dialog from "@mui/material/Dialog";
import DialogActions from "@mui/material/DialogActions";
import DialogContent from "@mui/material/DialogContent";
import DialogContentText from "@mui/material/DialogContentText";
import { Button } from "@mui/material";
import TextField from "@mui/material/TextField";

export default function Task({ todo }) {
  // استقبل todo من props
  const [open, setOpen] = useState(false);
  const [edit, setEdit] = useState(false);
  const [editedValue, setEditedValue] = useState({ title: "", details: "" });
  const [oldData, setOldData] = useState({
    title: todo.title,
    details: todo.taskDetails,
  });
  const { todo: todos, setTodo } = useContext(tasksContext);

  function handleCheckClick() {
    let updatedTodos = todos.map((t) =>
      t.id === todo.id ? { ...t, isDone: !t.isDone } : t
    );
    setTodo(updatedTodos);
    localStorage.setItem("todos", JSON.stringify(updatedTodos));
  }

  function handleDeleteClick() {
    let updatedTodos = todos.filter((t) => t.id !== todo.id);
    setTodo(updatedTodos);
    localStorage.setItem("todos", JSON.stringify(updatedTodos));
  }
  function handleEditClick() {
    let updatedTodos = todos.map((t) =>
      t.id === todo.id
        ? { ...t, title: editedValue.title, taskDetails: editedValue.details }
        : t
    );
    setTodo(updatedTodos);
    localStorage.setItem("todos", JSON.stringify(updatedTodos));
  }

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };
  const handleEditPageOpen = () => {
    setEdit(true);
  };

  const handleEditPageClose = () => {
    setEdit(false);
  };

  return (
    <div
      className="text"
      style={{
        padding: "0px 5px",
        display: "flex",
        justifyContent: "left",
        alignItems: "center",
        backgroundColor: "#252F88",
        margin: "20px 0",
        transition: ".3s",
      }}
    >
      <div
        style={{
          display: "flex",
          direction: "rtl",
          justifyContent: "space-around",
          alignItems: "center",
          fontSize: "6px",
        }}
      >
        {/* زر التحقق */}
        <IconButton aria-label="check" onClick={handleCheckClick}>
          <CheckIcon
            className="myIcon"
            style={{
              color: todo.isDone ? "white" : "#8bc34a",
              padding: "3px",
              background: todo.isDone ? "#8bc34a" : "white",
              border: "1px solid #8bc34a",
              borderRadius: "50%",
              fontSize: "20px",
              transition: ".3s",
            }}
          />
        </IconButton>

        {/* {dialog for delete} */}
        <Dialog
          open={open}
          onClose={handleClose}
          aria-labelledby="alert-dialog-title"
          aria-describedby="alert-dialog-description"
        >
          <DialogContent>
            <DialogContentText id="alert-dialog-description">
              هل أنت متأكد أنك تريد إتمام عملية الحذف ؟
            </DialogContentText>
          </DialogContent>
          <DialogActions>
            <Button onClick={handleClose}>إلغاء</Button>
            <Button
              onClick={() => {
                handleDeleteClick();
                handleClose();
              }}
              autoFocus
            >
              موافق
            </Button>
          </DialogActions>
        </Dialog>
        {/* {dialog for delete} */}

        {/*  */}
        {/*  */}
        {/*  */}

        {/* زر التعديل */}
        <IconButton
          aria-label="edit"
          onClick={() => {
            handleEditPageOpen();
          }}
        >
          <ModeEditOutlineOutlinedIcon
            className="myIcon"
            style={{
              color: "#1769aa",
              background: "white",
              border: "1px solid #1769aa",
              borderRadius: "50%",
              fontSize: "20px",
              transition: ".3s",
              padding: "3px",
            }}
          />
        </IconButton>

        {/* {dialog for Edit} */}
        <Dialog
          open={edit}
          onClose={handleEditPageClose}
          aria-labelledby="alert-dialog-title"
          aria-describedby="alert-dialog-description"
        >
          <DialogContent style={{ direction: "rtl" }}>
            <DialogContentText id="alert-dialog-description">
              تعديل المهمة
            </DialogContentText>
          </DialogContent>
          <DialogContent>
            <TextField
              value={oldData.title}
              id="outlined-basic"
              label="عنوان المهمة"
              variant="standard"
              style={{ width: "300px" }}
              onChange={(e) => {
                setEditedValue({ ...editedValue, title: e.target.value });
                setOldData(e.target.value);
              }}
            />
          </DialogContent>
          <DialogContent>
            <TextField
              value={oldData.details}
              id="outlined-basic"
              label="التفاصيل"
              variant="standard"
              style={{ width: "300px" }}
              multiline
              onChange={(e) => {
                setEditedValue({ ...editedValue, details: e.target.value });
                setOldData(e.target.value);
              }}
            />
          </DialogContent>
          <DialogActions>
            <Button onClick={handleEditPageClose}>إلغاء</Button>
            <Button
              onClick={() => {
                handleEditClick();
                handleEditPageClose();
              }}
              autoFocus
            >
              موافق
            </Button>
          </DialogActions>
        </Dialog>
        {/* {dialog for Edit} */}
        {/* زر الحذف */}
        <IconButton
          aria-label="delete"
          onClick={() => {
            handleClickOpen();
          }}
        >
          <DeleteOutlineIcon
            className="myIcon"
            style={{
              background: "white",
              border: "1px solid red",
              borderRadius: "50%",
              fontSize: "20px",
              transition: ".3s",
              padding: "3px",
            }}
            color="error"
          />
        </IconButton>
      </div>

      {/* نص المهمة */}
      <div
        style={{
          color: "white",
          flex: "1",
          marginLeft: "5px",
          textAlign: "right",
        }}
      >
        <h3 style={{ textDecoration: todo.isDone ? "line-through" : "none" }}>
          {todo.title}
        </h3>
        <h4>{todo.taskDetails}</h4>
      </div>
    </div>
  );
}
