import Container from "@mui/material/Container";
import Button from "@mui/material/Button";
import TextField from "@mui/material/TextField";
import Task from "./Task";
import { tasksContext } from "../context/TaskContext";
import { useState, useContext } from "react";
import { v4 as uuidv4 } from "uuid";

export default function TodoList() {
  // const { todo, setTodo } = useContext(tasksContext);
  const [titleInput, setTitleInput] = useState("");
  const allTodosList = todo.map((t) => {
    return <Task key={t.id} todo={t} handleCheck={handleCheckClick} />;
  });

  function handleAddClick() {
    if (titleInput !== "") {
      const newTodo = {
        id: uuidv4(),
        title: titleInput,
        taskDetails: "",
        isDone: false,
      };
      setTodo([...todo, newTodo]);
      setTitleInput("");
    }
  }

  function handleCheckClick(todoId) {
    let updatedTodo = todo.map((t) =>
      t.id === todoId ? { ...t, isDone: !t.isDone } : t
    );
    setTodo(updatedTodo);
  }

  return (
    <Container
      maxWidth="xs"
      style={{
        backgroundColor: "#eee",
        marginTop: "150px",
        padding: "10px 20px",
        borderRadius: "6px",
      }}
    >
      <h1 style={{ fontFamily: "A" }}>مهامي</h1>

      <div style={{ marginBottom: "20px" }}>
        <Button variant="outlined" color="error">
          غير منجز
        </Button>
        <Button variant="outlined" color="error">
          منجز
        </Button>
        <Button variant="outlined" color="error">
          الكل
        </Button>
      </div>
      {allTodosList}
      {/* <tasksContext.Provider value={Tasks}>
        <Task />
      </tasksContext.Provider> */}

      <div style={{ display: "flex", justifyContent: "center" }}>
        <Button
          variant="contained"
          color="error"
          style={{ width: "30%", marginRight: "5px" }}
          onClick={handleAddClick}
        >
          إضافة
        </Button>

        <TextField
          id="outlined-basic"
          label="عنوان المهمة"
          variant="outlined"
          color="error"
          onChange={(e) => {
            setTitleInput(e.target.value);
          }}
          value={titleInput}
          style={{ flex: "1" }}
        />
      </div>
    </Container>
  );
}
