import Icons from "./Icons";
import "./Task.css";
import { useContext } from "react";
import { tasksContext } from "../context/TaskContext";
export default function Task() {
  let myTaskContext = useContext(tasksContext);

  let idsNumber = Array(tasksContext).length;
  console.log(idsNumber);
  console.log(myTaskContext);
  return (
    <div
      key={myTaskContext.id}
      className="text"
      style={{
        padding: "0px 5px",
        display: "flex",
        justifyContent: "left",
        alignItem: "center",
        backgroundColor: "#252F88",
        margin: "20px 0",
        transition: ".3s",
      }}
    >
      <Icons />
      <div
        style={{
          color: "white",
          flex: "1",
          marginLeft: "5px",
          textAlign: "right",
        }}
      >
        <h4>{myTaskContext.text}</h4>
        <h6>{myTaskContext.taskDetails}</h6>
      </div>
    </div>
  );
}
